import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Konva from 'konva';
import { Stage, Layer, Star, Text, Rect } from 'react-konva';
import Rectangulo from './componentes/Rectangulo'

class App extends Component {

handleDragStart = e => {
  e.target.setAttrs({
    shadowOffset:{
      x: 15,
      y: 15
    },
    scaleX: 1.1,
    scaleY: 1.1
  });
};

handleDragEnd = e =>{
  e.target.to({
    duration: 0.5,
    easing: Konva.Easings.ElasticEaseOut,
    scaleX: 1,
    scaleY: 1,
    shadowOffsetX:5,
    shadowOffsetY: 5
  });
};

  render() {
    return (
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Text text = "Intenta dibujar aquÃ­" fontSize = "30" fill= "green" x= "20" y = "30" />
          <Rect x="20" y = "50" width="100" height= "50" fill="#00D2FF" stroke= "black" draggable="true" />
          <Rectangulo />
        </Layer>
      </Stage>
    );
  }
}

export default App;
