import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Konva from 'konva';
import { Stage, Layer, Star, Text, Rect,Util,Circle } from 'react-konva';
import Rectangulo from './componentes/Rectangulo'

class App extends Component {

      state = {
          layer : Konva.Layer,
          stage : Konva.Stage,
          mousePosX : 0,
          mousePosY : 0
        }

  componentDidMount(){
      this.paintContainer()
      this.avisoTest()
    /*  this.state.stage.on('click', function (e) {

        this.setState ({
            mousePosY : e.evt.y,
            mousePosX : e.evt.x
        })
      });*/
  }

createCircle = () =>{
  this.state.layer.add(new Konva.Circle({
              x: 10,
              y: 10,
              fill: 'red',
              radius: 20
          }))
          this.state.layer.draw();
    }

    handleClick = () => {
              console.log("desde el drag!");
    }

paintContainer = () =>{
  this.state.stage = new Konva.Stage({
       container: 'container',
       width: window.innerWidth,
       height: window.innerHeight
     });

     var text = new Konva.Text({
           x: 10,
           y: 10,
           fontFamily: 'Calibri',
           fontSize: 20,
           text: 'hola',
           fill: 'black'
         });

    this.state.layer = new Konva.Layer();

    this.state.layer.add(new Konva.Circle({
                x: 100,
                y: 100,
                fill: 'green',
                radius: 20,
                onMouseDown: this.handleClick()
            }))
     this.state.layer.add(text);
     this.state.stage.add(this.state.layer);
}

avisoTest = () => {
  this.state.layer.add(new Konva.Circle({
              x: 120,
              y: 120,
              fill: 'red',
              radius: 20
          }))
          this.state.layer.draw();
}

  render() {
    return (
       <div id="container">
       <Circle
           x={200} y={100} radius={50} fill="yellow"
       />
       </div>
    );
  }
}

export default App;
