import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Konva from 'konva';

import Mundo from './componentes/Mundo';

class App extends Component {

  state = {
    rectangulos: {}
  }

  componentDidMount(){
    this.setState({
       rectangulos : [{
                     width: 100,
                     height: 200,
                     x: 100,
                     y: 100,
                     fill: 'black',
                     radius: 20
                   }],
       selectedShapeName: ""
    })
  }

  selectShapeNameFuncion = (name) =>{
    this.setState({
      selectedShapeName: name
    })
  }

  insertarRectangulo = (e) => {

  const rectangulos = {...this.state.rectangulos};
  rectangulos[`rectangulo${Date.now()}`] =   {
                width: 100,
                height: 200,
                x: e.evt.x,
                y: e.evt.y,
                fill: 'black',
                radius: 20
            };
            this.setState({
              rectangulos
            })
            //this.forceUpdate();
}

  render() {
    return (
        <Mundo
          selectedShapeName = {this.state.selectedShapeName}
          rects= { this.state.rectangulos }
          insertarRectangulo = { this.insertarRectangulo }
          selectShapeName = {this.selectShapeNameFuncion}
        />
    );
  }
}

export default App;
