import { Stage, Layer, Star, Text, Rect,Util } from 'react-konva';
import Rectangulo from './componentes/Rectangulo'

class App extends Component {
  constructor(props) {
      super(props);
    this.state = {
        layer : Konva.Layer,
        stage : Konva.Stage,
        mousePosX : 0,
        mousePosY : 0
      }
      this.handleClick = this.handleClick.bind(this);
    }



  handleClick = () => {
            console.log("hola mundo!!");
  }

  componentDidMount(){
      this.paintContainer()
      this.avisoTest()
      /*this.setState ({
          mousePosY : e.evt.y,
          mousePosX : e.evt.x
      })*/
      window.addEventListener("resize", this.checkSize);
    /*  this.state.stage.on('click', function (e) {
            this.createCircle();

      })*/
  }


createCircle = function (){
  this.state.layer.add(new Konva.Circle({
              x: 10,
              y: 10,
              fill: 'red',
              radius: 20
          }))
          this.state.layer.draw();
    }
    checkSize = () => {
        const width = this.container.offsetWidth;

      };


paintContainer = () =>{
  this.state.stage = new Konva.Stage({
       container: 'container',
       width: window.innerWidth,
       height: window.innerHeight,
       onClick: this.handleClick()
     });

     var text = new Konva.Text({
           x: 10,
           y: 10,
           fontFamily: 'Calibri',
           fontSize: 20,
           text: 'hola',
           fill: 'black'
         });

    this.state.layer = new Konva.Layer();

    this.state.layer.add(new Konva.Circle({
                x: 100,
                y: 100,
                fill: 'red',
                radius: 20

            }))
     this.state.layer.add(text);
     this.state.stage.add(this.state.layer);
}

avisoTest = () => {
  this.state.layer.add(new Konva.Circle({
              x: 120,
              y: 120,
              fill: 'red',
              radius: 20
          }))
          this.state.layer.draw();
}

  render() {
    return (
       <div id="container">
       </div>
    );
  }
}

export default App;
