import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Konva from 'konva';
import { Stage, Layer, Star, Text, Rect } from 'react-konva';
import Rectangulo from './componentes/Rectangulo'

class App extends Component {

  state = {
    layer : Konva.Layer,
    stage : Konva.Stage,
    mousePosX : 0,
    mousePosY : 0
  }


  componentDidMount(){
      this.paintContainer()
      this.avisoTest()
  }

createCircle = (posX,posY) =>{
  this.state.layer.add(new Konva.Circle({
              x: posX,
              y: posY,
              fill: 'red',
              radius: 20
          }))
          this.state.layer.draw();
}


paintContainer = () =>{
  this.state.stage = new Konva.Stage({
       container: 'container',
       width: window.innerWidth,
       height: window.innerHeight
     });

     var text = new Konva.Text({
           x: 10,
           y: 10,
           fontFamily: 'Calibri',
           fontSize: 20,
           text: 'hola',
           fill: 'black'
         });
    this.state.layer = new Konva.Layer();

    this.state.stage.on('click', function (e) {
        //this.state.mousePosY = e.evt.y;
        this.createCircle(e.evt.x,e.evt.y);
    });
    this.state.layer.add(new Konva.Circle({
                x: 100,
                y: 100,
                fill: 'red',
                radius: 20
            }))
     this.state.layer.add(text);
     this.state.stage.add(this.state.layer);
}

avisoTest = () => {
  this.state.layer.add(new Konva.Circle({
              x: 120,
              y: 120,
              fill: 'red',
              radius: 20
          }))
          this.state.layer.draw();
}

  render() {
    return (
       <div id="container">
       </div>
    );
  }
}

export default App;
