import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Konva from 'konva';

import Mundo from './componentes/Mundo';

class App extends Component {

  state = {
    rectangulos: {},
    circulos: {},
    textos: {}
  }

  componentDidMount(){
    this.setState({
       rectangulos : [{
                     width: 100,
                     height: 200,
                     x: 100,
                     y: 100,
                     fill: 'black',
                     radius: 20,
                     name: "rect1"
                   }],
       circulos : [{
         radius: 50,
         x : 200,
         y: 100,
         fill: "red",
         name: "circle1"
       }],
       textos : [{
         name : "texto1",
         x : 150,
         y : 150,
         fontSize : 30,
         text : "ttt mundo",
         fontFamily : "Calibri",
         fill: "red"
       }],
       selectedShapeName: ""
    })
  }

  selectShapeNameFuncion = (name) =>{
    this.setState({
      selectedShapeName: name
    })
  }

insertarRectangulo = (e) => {

  const rectangulos = {...this.state.rectangulos};
  rectangulos[`rectangulo${Date.now()}`] =   {
                width: 100,
                height: 200,
                x: e.evt.x,
                y: e.evt.y,
                fill: 'black',
                radius: 20
            };
            this.setState({
              rectangulos
            })
}


insertarCirculo = (e) => {
  const circulos = {...this.state.circulos};
  circulos[`circulo{Date.now()}`] =   {
                radius: 50,
                x : e.evt.x,
                y: e.evt.y,
                fill: "red"
            };
            this.setState({
                circulos
            })

}

insertarText = (e) => {
  const textos = {...this.state.textos};
  textos[`texto{Date.now()}`] =   {
                x : e.evt.x,
                y : e.evt.y,
                fontSize : 30,
                text : "hola",
                fontFamily : "Calibri",
                fill: "green"
            };
            this.setState({
                textos
            })

}


  render() {
    return (
        <Mundo
          selectedShapeName = {this.state.selectedShapeName}
          rects= { this.state.rectangulos }
          circulos = {this.state.circulos}
          textos = {this.state.textos}
          insertarCirculo = {this.insertarCirculo}
          insertarRectangulo = { this.insertarRectangulo }
          selectShapeName = {this.selectShapeNameFuncion}

        />
    );
  }
}

export default App;
