import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Konva from 'konva';
import { Stage, Layer, Star, Text, Rect,Util } from 'react-konva';
import Mundo from './componentes/Mundo';

class App extends Component {

  state = {
    rectangulos: {}
  }

  componentDidMount(){
    this.setState({
       rectangulos : [
              {
                  width: 100,
                  height: 200,
                  x: 3,
                  y: 100,
                  fill: 'green',
                  radius: 20
              },
              {
                          width: 200,
                          height: 100,
                          x: 4,
                          y: 160,
                          fill: 'blue',
                          radius: 20
                      },
                      {
                                  width: 100,
                                  height: 200,
                                  x: 3,
                                  y: 400,
                                  fill: 'red',
                                  radius: 20
                              }
            ]
    })
  }

insertarRectangulo = () => {
  const rectangulos = {...this.state.rectangulos};
  rectangulos[`rectangulo${Date.now()}`] =   {
                width: 100,
                height: 200,
                x: 3,
                y: 400,
                fill: 'red',
                radius: 20
            };
            this.setState({
              rectangulos
            })
}

  render() {
    return (
        <Mundo
          rects= { this.state.rectangulos }
          insertarRectangulo = { this.insertarRectangulo }
        />
    );
  }
}

export default App;
