import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Konva from 'konva';
import { Stage, Layer, Star, Text, Rect } from 'react-konva';
import Rectangulo from './componentes/Rectangulo'

class App extends Component {
  layer = new Konva.Layer();
  componentDidMount(){
      this.paintContainer()
  }

paintContainer = () =>{
  var stage = new Konva.Stage({
       container: 'container',
       width: window.innerWidth,
       height: window.innerHeight
     });

     var text = new Konva.Text({
           x: 10,
           y: 10,
           fontFamily: 'Calibri',
           fontSize: 20,
           text: 'hola',
           fill: 'black'
         });
    layer.add(new Konva.Circle({
                x: 100,
                y: 100,
                fill: 'red',
                radius: 20
            }))
     layer.add(text);
     stage.add(layer);
}
handleDragStart = e => {
  e.target.setAttrs({
    shadowOffset:{
      x: 15,
      y: 15
    },
    scaleX: 1.1,
    scaleY: 1.1
  });
};

handleDragEnd = e =>{
  e.target.to({
    duration: 0.5,
    easing: Konva.Easings.ElasticEaseOut,
    scaleX: 1,
    scaleY: 1,
    shadowOffsetX:5,
    shadowOffsetY: 5
  });
};

avisoTest = () => {
  layer.add(new Konva.Circle({
              x: 100,
              y: 100,
              fill: 'red',
              radius: 20
          }))
}

  render() {
    return (
       <div id="container">

       </div>
    );
  }
}

export default App;
