import React, { Component } from 'react';
import { render } from 'react-dom';
import { Stage, Layer, Rect } from 'react-konva';

class Rectangulo extends React.Component {
  changeSize = () => {
    // to() is a method of `Konva.Node` instances
    this.rect.to({
      scaleX: Math.random() + 0.8,
      scaleY: Math.random() + 0.8,
      duration: 0.2
    });
  };

  /*test = () => {
    this.rect.on('mousedown touchstart', function() {

          this.props.avisoTest();
        });
  };*/
  test = (e) =>{
    //  e.preventDefault();
        this.props.avisoTest();
  }

  render() {
    return (
      <Rect
        ref={node => {
          this.rect = node;
        }}
        x = {this.props.rectangulo.x}
        y = {this.props.rectangulo.y}
        width= {this.props.rectangulo.width}
        height= {this.props.rectangulo.height}
        fill= {this.props.rectangulo.fill}
      >
        <Transformer
          rotateAnchorOffset = {60}
          />
      </Rect>
    );
  }
}

export default Rectangulo;
